import React from 'react';
// import css from 'index.css';
// import Grid from '@material-ui/core/Grid';

type Props = {
    name: string,
    photoPath: string,
    description: string,
    linkedIn: string,
    github: string,
    goodleScholar: string
  }



export default function About({ name, photoPath, description, linkedIn, github, goodleScholar }: Props){
    return (
        <section id={"about"}>
            <h1 className={"title"}>{name}</h1>
            <div className={css.row}>
                {/* <Grid container> */}
                    {/* <Grid item xs={6}> */}
                        <center><img className={"profile-img"} src={photoPath} /></center>
                    {/* </Grid> */}
                    {/* <Grid item xs={6}> */}
                        <p>{description}</p>
                        <br />
                        <a href="mailto:jinhanchoi@snu.ac.kr" target="_top"> <i class="fas fa-envelope"></i></a> |
                        <a href={linkedIn} target="_blank"> <i className="fab fa-linkedin"></i></a> |
                        <a href={github} target="_blank"> <i className="fab fa-github"></i></a> |
                        <a href={googleScholar} target="_blank"> <i className="fas fa-graduation-cap"></i> Google Scholar</a>
                        <br />
                    {/* </Grid> */}
                {/* </Grid> */}
            </div>
        </section>
    );
}

