import React from 'react';
// import css from '../index.css';
import {Grid, Box, Typography} from '@mui/material';

type AboutProps = {
    name: string,
    photoPath: string,
    description: string,
    linkedIn: string,
    github: string,
    googleScholar: string
}

const About: React.FC<AboutProps> = ({ name, photoPath, description, linkedIn, github, googleScholar }) => {
    return (
        <Grid container spacing={3}>
            <Grid item>
                <Typography variant="h4" align="left">Jinhan Choi</Typography>
            </Grid>

            <Grid item container direction="row" spacing={5}>
                <Grid item xs={3}>
                    <img src={photoPath} width="100%" style={{ boaderRadius:"50%", objectFit:"cover"}} />
                </Grid>
                <Grid item xs align="left">
                    <Box>
                        {description}
                    </Box>
                    <Box>
                        <a href="mailto:jinhanchoi@snu.ac.kr" target="_top"> <i className="fas fa-envelope"></i></a> |
                        <a href={linkedIn} target="_blank"> <i className="fab fa-linkedin"></i></a> |
                        <a href={github} target="_blank"> <i className="fab fa-github"></i></a> |
                        <a href={googleScholar} target="_blank"> <i className="fas fa-graduation-cap"></i> Google Scholar</a>
                    </Box>
                </Grid>
            </Grid>
        </Grid>
    )
}

export default About;

