import React from 'react';
// import css from '../index.css';
import Grid from '@mui/material/Grid';

type AboutProps = {
    name: string,
    photoPath: string,
    description: string,
    linkedIn: string,
    github: string,
    googleScholar: string
}

const About: React.FC<AboutProps> = ({ name, photoPath, description, linkedIn, github, googleScholar }) => {
    return (
        <section id={"about"}>
            <h1>{name}</h1>
            <div>
            <Grid container direction="row">
                <Grid item xs={3}>
                    <img className={"profile-img"} src={photoPath} />
                </Grid>
                <Grid item xs container>
                    <Grid item xs>
                        {description}
                    </Grid>
                    <Grid item xs>
                        <a href="mailto:jinhanchoi@snu.ac.kr" target="_top"> <i className="fas fa-envelope"></i></a> |
                        <a href={linkedIn} target="_blank"> <i className="fab fa-linkedin"></i></a> |
                        <a href={github} target="_blank"> <i className="fab fa-github"></i></a> |
                        <a href={googleScholar} target="_blank"> <i className="fas fa-graduation-cap"></i> Google Scholar</a>
                    </Grid>
                        </Grid>
            </Grid>
                    <br />

                        <br />
                    {/* </Grid> */}
                {/* </Grid> */}
            </div>
        </section>
    )
}

export default About;

