import React, { useRef, useEffect, useState } from 'react';
import { Grid, TextField, Typography } from '@mui/material';

type INewsItem = {
  title: string;
  date: string;
};

type NewsProps = {
  news: Array<INewsItem>;
};

const News: React.FC<NewsProps> = (props) => {
  const newsSearchRef = useRef();
  const [newsItems, setNewsItems] = useState(props.news);
  // const [filteredNewsItems, setFilteredNewsItems] = useState(props.news.slice(0, 5));

  function handleNewsFilter() {
    // const data = props.news;
    newsSearchRef.current.addEventListener('input', function (event) {
      // console.log('value', this.value);
      if (this.value !== '') {
        const filtered = newsItems.filter((d) => {
          const text = d.title + ' ' + d.date;
          return text.toLowerCase().includes(this.value.toLowerCase());
        });
        setNewsItems(filtered.slice(0, 5));
        // NewsItem(newsItems);
      } else {
        setNewsItems(newsItems.slice(0, 5));
        // NewsItem();
      }
    });
  }

  useEffect(() => {
    if (newsItems) {
      handleNewsFilter();
    }
  }, [newsItems]);

  // console.log(props);
  return (
    <Grid container spacing={3}>
      <Grid item>
        <Typography variant="h4" align="left">
          News
        </Typography>
      </Grid>
      <Grid item xs>
        <TextField variant="outlined" placeHolder="Search News..." ref={newsSearchRef} />
      </Grid>
      <Grid item container spacing={3}>
        {newsItems && newsItems.map((news) => NewsItem(news))}{' '}
      </Grid>
    </Grid>
  );
};

const NewsItem: React.FC<INewsItem> = ({ title, date }) => {
  // let news = newsItems;
  // news = news.slice(0,5);
  // // console.log(news);
  // return news.map(d => (
  return (
    <div className={'row'}>
      <div className={'col-8'}>{title}</div>
      <div className={'col-4'}>{date}</div>
    </div>
  );
};

export default News;
