import React, { useContext } from 'react';

type IProjectItem = {
    id: string,
    title: string,
    authors: string,
    source: string,
    teaser: string,
    materials: [],
}


type ProjectsProps = {
    projects: Array<IProjectItem>
}

const Projects: React.FC<ProjectsProps> = (props) => {
    console.log(props)
    return (
        <section id={"projects"}>
            <h1 className={"title"}>Publications</h1>
            <div className={"project-list"}>
                {props.projects && props.projects.map((project) => (
                    ProjectItem(project)
                ))}
            </div>
        </section>
    );
}

const materials = (d) => d.materials.map((m) => 
    <span>
        <a href={m.path} target="_blank"><i class="fas fa-file-alt"></i> {m.label}</a>
    </span>
);

const ProjectItem: React.FC<IProjectItem> = ({id, title, authors, source, teaser, materials}) => {
    return (
        <div className={"row"}>
            {/* <Grid container>
                <Grid item xs={6}> */}
                {/* <div className={"col-6"}> */}
                    <div className={"project-title"}>
                        {title}
                        {/* <Link to={`/project/${d.id}`}><strong>{d.title}</strong></Link> */}
                        {/* <Link><strong>{d.title}</strong></Link> */}
                        {/* <strong>{d.title}</strong> */}
                    </div>
                    <div className={"project-authors"}>
                        {authors}<br/>
                    </div>
                    <div className={"project-source"}>
                        <em>{source}</em>
                    </div>
                    <div class="project-materials">
                        {materials(materials)}
                    </div>
                {/* </div>  */}
                {/* </Grid> */}
            {/* <div className={"col-6"}> */}
                {/* // <Grid item xs={6}> */}
                    <img src={teaser} width="100%"/>
                {/* </Grid> */}
            {/* </div> */}
            {/* </Grid> */}
        </div>
    );
}

