import React from 'react';
import { Grid, Box, Typography } from '@mui/material';

type IProjectItem = {
  id: string;
  title: string;
  authors: string;
  source: string;
  teaser: string;
  materials: [];
};

type ProjectsProps = {
  projects: Array<IProjectItem>;
};

const Projects: React.FC<ProjectsProps> = (props) => {
  return (
    <Grid container spacing={3}>
      <Grid item>
        <Typography variant="h5" align="left">
          Publications
        </Typography>
      </Grid>
      <Grid item container spacing={3}>
        {props.projects && props.projects.map((project) => ProjectItem(project))}
      </Grid>
    </Grid>
  );
};

const fnMaterials = (materials: []) => materials.map((m) =>
    <span>
        <a href={m.path} target="_blank"><i class="fas fa-file-alt"></i> {m.label}</a>
    </span>
);

const ProjectItem: React.FC<IProjectItem> = ({ id, title, authors, source, teaser, materials }) => {
  return (
    <Grid item container direction="row" spacing={3}>
      <Grid item xs={2}>
        <img src={teaser} width="100%" />
      </Grid>

      <Grid item xs align="left">
        <Box fontWeight="fontWeightBold">{title}</Box>
        <Box>{authors}</Box>
        <Box fontStyle="italic">{source}</Box>

      </Grid>
    </Grid>
  );
};

export default Projects;
