                    </div>
                    <div className={"project-authors"}>
                        {d.authors}<br/>
                    </div>
                    <div className={"project-source"}>
                        <em>{d.source}</em>
                    </div>
                    <div class="project-materials">
                        {materials(d)}
                    </div>
                {/* </div>  */}
                </Grid>
            {/* <div className={"col-6"}> */}
                <Grid item xs={6}>
                    <img src={d.teaser} width="100%"/>
                </Grid>
            {/* </div> */}
            </Grid>
        </div>
    ));
}


