import React from 'react';
// import css from '../index.css';
// import Grid from '@material-ui/core/Grid';

type AboutProps = {
        name: string,
        photoPath: string,
        description: string,
        linkedIn: string,
        github: string,
        googleScholar: string
  }



const About: React.FC = ({ name, photoPath, description, linkedIn, github, googleScholar }: AboutProps) => (
        <section id={"about"}>
            <h1>{name}</h1>
            <div>
                {/* <Grid container> */}
                    {/* <Grid item xs={6}> */}
                        <img className={"profile-img"} src={photoPath} />
                    {/* </Grid> */}
                    {/* <Grid item xs={6}> */}
                        <p>{description}</p>
                        <br />
                        <a href="mailto:jinhanchoi@snu.ac.kr" target="_top"> <i className="fas fa-envelope"></i></a> |
                        <a href={linkedIn} target="_blank"> <i className="fab fa-linkedin"></i></a> |
                        <a href={github} target="_blank"> <i className="fab fa-github"></i></a> |
                        <a href={googleScholar} target="_blank"> <i className="fas fa-graduation-cap"></i> Google Scholar</a>
                        <br />
                    {/* </Grid> */}
                {/* </Grid> */}
            </div>
        </section>
)

export default About;

