import React, { useState } from 'react';
import { Grid, Box, Typography, Chip } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  publicationTeaser: {
    backgroundSize: 'cover',
    backgroundPosition: 'top left',
    transition: 'all 2s',
    width: '20vw',
    // minHeight: '8rem',
    // maxHeight: '14rem',
    // marginLeft: '13px',
  },
  publicationTeaserHover: {
    backgroundPosition: 'botton right',
    width: '20vw',
  },
}));

type IProjectItem = {
  id: string;
  title: string;
  authors: string;
  source: string;
  teaser: string;
  materials: [];
  award: string;
};

type ProjectsProps = {
  projects: Array<IProjectItem>;
};

const Projects: React.FC<ProjectsProps> = (props) => {
  return (
    <Grid container spacing={3}>
      <Grid item>
        <Box fontWeight="fontWeightBold" fontSize="h5.fontSize">
          Publications
        </Box>
      </Grid>
      <Grid item container spacing={3}>
        {props.projects && props.projects.map((project) => ProjectItem(project))}
      </Grid>
    </Grid>
  );
};

type MaterialsType = {
  label: string;
  path: string;
};
const fnMaterials = (materials: MaterialsType) =>
  materials.map((m) => (
    <span>
      <a href={m.path} target="_blank">
        <i className="fas fa-file-alt"></i>
        <Typography variant="button"> {m.label}</Typography>
      </a>
    </span>
  ));

const fnAuthors = (authors: string) => {
  const newAuthors = authors.replace('Jinhan Choi', '<strong>Jinhan Choi</strong>');

  return '<Box component="div">' + newAuthors + '</Box>';
};

const ProjectItem: React.FC<IProjectItem> = ({
  id,
  title,
  authors,
  source,
  teaser,
  materials,
  award,
}) => {
  const classes = useStyles();
  const [hovered, setHovered] = useState(false);
  return (
    <Grid item container direction="row" spacing={3}>
      <Grid item xs={2}>
        <div className={classes.publicationTeaser}>
          <img
            src={teaser}
            width="100"
            // height="100%"
            // onMouseOver={() => setHovered(true)}
            // onMouseOut={() => setHovered(false)}
            // className={hovered ? classes.publicationTeaserHover : classes.publicationTeaser}
          />
        </div>
      </Grid>

      <Grid item xs container align="left" direction="column" spacing={0.5}>
        <Grid item xs>
          <Box fontWeight="fontWeightBold">{title}</Box>
        </Grid>
        {/* {fnAuthors(authors)} */}
        <Grid item xs>
          <div dangerouslySetInnerHTML={{ __html: fnAuthors(authors) }} />
          {/* {fnAuthors(authors)} */}
        </Grid>
        <Grid item xs>
          <Box fontStyle="italic">{source}</Box>
        </Grid>

        {award && (
          <Grid item xs>
            <Box>
              <Chip
                size="small"
                color="primary"
                label={award}
                style={{ color: 'white', fontWeight: 'bold' }}
              />
            </Box>
          </Grid>
        )}

        <Grid item xs>
          <Box>{fnMaterials(materials)}</Box>
        </Grid>
      </Grid>
    </Grid>
  );
};

export default Projects;
