import React, { useRef, useEffect, useState } from 'react';

interface NewsProps {
    news: Array<Object>
}

const News: React.FC<NewsProps> = (news) => {
    const newsSearchRef = useRef();
    const [newsItems, setNewsItems] = useState(news);

    function NewsItems(){
        let _news = newsItems;
        _news = _news.slice(0,5);
        // console.log(news);
        return _news.map(d => (
            <div className={"row"}>
                <div className={"col-8"}>
                    {d.title}
                </div>
                <div className={"col-4"}>
                    {d.date}
                </div>
            </div>
        ));
    }

    function handleNewsFilter(){
        let _news = newsItems
        newsSearchRef.current.addEventListener('input', function(event){
            // console.log('value', this.value);
            if (this.value !== ''){
                let filtered = _news.filter(d => {
                    let text = (d.title + ' ' + d.date);
                    return text.toLowerCase().includes(this.value.toLowerCase());
                });
                setNewsItems(filtered);
                NewsItems();
            } else {
                setNewsItems(_news);
                NewsItems();
            }
        });
    }

    useEffect(() => {
        handleNewsFilter(news);
    }, []);

    // console.log(props);
    return (
        <section id="news">
            <h1 className={"title"}>News</h1>
            <div className={"search"}>
                <input type={"search"} name={'news'} placeholder={"Search News..."} ref={newsSearchRef}/>
            </div>
            <div className={"news-list"}>
                {NewsItems(news)}
            </div>
        </section>
    );
}


export default News;
