import React, { useRef, useEffect, useState } from 'react';
import { Grid, TextField, Typography } from '@mui/material';

type INewsItem = {
  title: string;
  date: string;
};

type NewsProps = {
  news: Array<INewsItem>;
};

const News: React.FC<NewsProps> = (props) => {
  //   const newsSearchRef = useRef();
  const [newsItems, setNewsItems] = useState(props.news);

  const handleSearchNewsInput = (event) => {
    const searchQueryText = event.target.value;
    if (searchQueryText !== '') {
      const filtered = newsItems.filter((d) => {
        const text = d.title + ' ' + d.date;
        return text.toLowerCase().includes(searchQueryText.toLowerCase());
      });
      setNewsItems(filtered.slice(0, 5));
    } else {
      setNewsItems(newsItems.slice(0, 5));
    }
  };

  return (
    <Grid container spacing={3} direction="column">
      <Grid item xs>
        <Typography variant="h5" align="left">
          News
        </Typography>
      </Grid>
      <Grid item xs>
        <TextField
          variant="outlined"
          placeholder="Search News..."
          fullWidth
          size="small"
          onChange={handleSearchNewsInput}
        />
      </Grid>
      <Grid item container spacing={3}>
        {newsItems && newsItems.map((news) => NewsItem(news))}
      </Grid>
    </Grid>
  );
};

const NewsItem: React.FC<INewsItem> = ({ title, date }) => {
  // let news = newsItems;
  // news = news.slice(0,5);
  // // console.log(news);
  // return news.map(d => (
  return (
    <Grid item container>
      <Grid item xs={8} align="left">
        <Typography variant="subtitle2">{title}</Typography>
      </Grid>
      <Grid item xs={4} align="right">
      <Typography variant="subtitle2">{date}</Typography>
        {date}
      </Grid>
    </Grid>
  );
};

export default News;
