import React, { useRef, useEffect, useState } from 'react';

type INewsItem = {
    title: string,
    date: string,
}

type NewsProps = {
    news: Array<INewsItem>
}


const News: React.FC<NewsProps> = (props) => {
    const newsSearchRef = useRef();
    const [newsItems, setNewsItems] = useState(props.news);



    function handleNewsFilter(){
        const data = newsItems;
        newsSearchRef.current.addEventListener('input', function(event){
            // console.log('value', this.value);
            if (this.value !== ''){
                const filtered = data.filter(d => {
                    const text = (d.title + ' ' + d.date);
                    return text.toLowerCase().includes(this.value.toLowerCase());
                });
                setNewsItems(filtered);
                return <NewsItems />;
            } else {
                setNewsItems(data.news);
                <NewsItems />;
            }
        });
    }

    useEffect(() => {
        handleNewsFilter();
    }, []);

    // console.log(props);
    return (
        <section id="news">
            <h1 className={"title"}>News</h1>
            <div className={"search"}>
                <input type={"search"} name={'news'} placeholder={"Search News..."} ref={newsSearchRef}/>
            </div>
            <div className={"news-list"}>
                <NewsItems />
            </div>
        </section>
    );
}

const NewsItem: React.FC<INewsItem> = ({}) => {
    let news = newsItems;
    news = news.slice(0,5);
    // console.log(news);
    return news.map(d => (
        <div className={"row"}>
            <div className={"col-8"}>
                {d.title}
            </div>
            <div className={"col-4"}>
                {d.date}
            </div>
        </div>
    ));
    }

export default News;
