    return (
        <Grid item container direction="row" spacing={3}>
            <Grid item xs={2}>
                <img src={teaser} width="100%"/>
            </Grid>

            <Grid item xs align="left">
                <Box fontWeight="fontWeightBold">
                    {title}
                </Box>
                <Box>
                    {authors}
                </Box>
                <Box>
                    {source}
                </Box>
            </Grid>
        </Grid>
    );
}

