    authors: string;
    teaser: string;
    materials: []
}

interface ProjectsProps {
    projects: Array<ProjectItemsProps>
}

const Projects: React.FC<ProjectsProps> = (props) => {
    return (
        <section id={"projects"}>
            <h1 className={"title"}>Publications</h1>
